import{e as i}from"./MainNavbar-950b43f9.js";import{d as t,o,c as s,b as e}from"./index-5bba7b53.js";const c="/Wikidata-logo.png",d={class:"article"},n=e('<h1 class="font-size--xxl font-black deco" id="%E4%BB%80%E9%BA%BC%E6%98%AF-wikidata%EF%BC%9F" tabindex="-1">什麼是 Wikidata？</h1><p class="wikidata-img"><img src="'+c+'" alt=""></p><p class="font-size--m">Wikidata (中文：維基數據、維基資料)是一個自由的協作式的多語言輔助資料庫，成立於 2012 年 10 月 29 日，屬於維基媒體運動的一環。其用於收集結構化的數據，並以鏈結開放資料的方式呈現，旨在支援維基百科、維基共享資源以及其他維基媒體運動中的項目，也支援世界上的每一個人。</p><hr><h1 class="font-size--xxl font-black deco" id="%E9%97%9C%E6%96%BC-wikidatacon" tabindex="-1">關於 Wikidatacon</h1><p class="font-size--m">Wikidatacon (中文：維基數據雙年會、維基資料雙年會) 自 2017 年在德國舉辦、2021 年起改為巡迴舉辦的 Wikidata 雙年會議。</p><p class="font-size--m">Wikidatacon 中，我們關注所有與 Wikidata 相關的廣泛事務，包含：Wikidata 的編輯、使用以及技術研究，Wikidata 平台技術 Wikibase 的相關議題，Wikidata 與其他維基媒體計畫或其他平台的整合應用以及 Wikidata 與整體鏈結開放資料的生態系發展，都是這個會議中關注的議題。</p><p class="font-size--m">2023 年德國維基媒體協會(WMDE)與台灣維基媒體協會(WMTW)偕同臺灣維基數據社群(Wikidata Taiwan)，在臺灣舉辦線上實體混合形式的會議，以「鏈結資料Koh發現」為號召，更加關注整個 Wikidata 生態系如何呈現在地資料的多樣性。</p><p class="wdc-img"><img src="'+i+'" alt=""></p><hr><h1 class="font-size--xxl font-black deco" id="%E5%BE%B5%E7%A8%BF%E8%A8%8A%E6%81%AF" tabindex="-1">徵稿訊息</h1><p class="font-size--m">Wikidatacon 作為全球最大的 Wikidata 交流會議，我們關注的不單單只有 Wikidata 本身，包含提供 Wikidata 的平台技術：Wikibase，以及從 Wikidata 延伸出去的鏈結資料生態系，都是我們討論的焦點。</p><p class="font-size--m">今年主題＂鏈結資料 Koh（再）發現：Wikidata 的跨領域匯流＂，主題將著重在跨領域資料的交流，並且更加重視在地資料如何與國際對話與鏈結。在今年度的徵稿中，我們重視資料的多樣性在 Wikidata、Wikibase 上被呈現，包含但不限於在地的語言、文化、民俗、藝術與知識。</p><p class="font-size--sm box-modifier--extrude-outside--top--8"><a href="/cfp" class="button box-modifier--extrude-outside--top--4">徵稿說明</a> <a href="https://pretalx.com/wikidatacon2023/" class="button box-modifier--extrude-outside--top--4">立即投稿</a></p>',14),p=[n],f={},x="",h=t({__name:"homepage.component",setup(r,{expose:a}){return a({frontmatter:{},excerpt:void 0}),(k,l)=>(o(),s("div",d,p))}});export{h as default,x as excerpt,f as frontmatter};
